        function getTargetValue(element)
        {
            return parseFloat($(element).val());
        }
        function getMaxRangeTargetSlider(differnce,target,opponent,targetValue,opponentValue)
        {
           let targetId=$(target).attr("id");
            let desiredValue=null;
            if(targetId === "min")
                {
                  desiredValue=opponentValue-(differnce);
                    blockMinValue(desiredValue);
                    return desiredValue;
                }
            if(targetId === "max")
            {
                desiredValue=opponentValue+differnce;
                blockMaxValue(desiredValue);
                return desiredValue;
            }
        }
        function blockMinValue(max)
        {
            let targetValue=parseFloat($("#min").val());
            if(targetValue >= max)
                {
                    console.log("Range Exceed for Min");
                    $("#min").val(max);
                }
        }
         function blockMaxValue(min)
        {
            let targetValue=parseFloat($("#max").val());
            if(targetValue <= min)
                {
                    console.log("Range Exceed for Max");
                    $("#max").val(min);
                }
        }
